#import "UIColor+VFCAdditions.h"
#import "VFCUtilities.h"

#pragma mark - UIColor

#pragma mark - Public Implementation

@implementation UIColor (VFCAdditions)

+ (UIColor *)venturaBlueColor {
    return [UIColor colorWithRed:32.0/255.0 green:33.0/255.0 blue:96.0/255.0 alpha:1.0];
}

+ (UIColor *)venturaBlackColor {
    return [UIColor colorWithRed:7.0/255.0 green:8.0/255.0 blue:8.0/255.0 alpha:1.0];
}

@end
